  color: #7ef1d2;
}

.theme-stack ul {
  list-style: disc inside;
  margin: 0.5rem 0 0;
  color: #d6e1ff;
  padding-left: 1rem;
}

.insight-card.reflections ol {
  margin: 0;
  padding-left: 1.1rem;
  color: #e7edff;
}

.insight-card.action-plan ul {
  margin: 0;
  padding: 0;
  list-style: none;
}

.insight-card.action-plan li {
  display: flex;
  gap: 0.75rem;
  margin-bottom: 0.75rem;
}

.insight-card.action-plan span {
  font-size: 0.85rem;
  color: #7ef1d2;
  min-width: 4rem;
}

.history-list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  gap: 0.85rem;
}

.history-list button {
  width: 100%;
  text-align: left;
  padding: 0.9rem;
  border-radius: 1rem;
  border: 1px solid rgba(255, 255, 255, 0.08);
  background: rgba(255, 255, 255, 0.02);
  color: inherit;
  cursor: pointer;
}

.history-list strong {
  display: inline-block;
  margin-left: 0.5rem;
  color: #7ef1d2;
}

.muted {
  color: #9aaccc;
}

.empty-state {
  border: 1px dashed rgba(255, 255, 255, 0.2);
  border-radius: 1rem;
  padding: 2rem;
  text-align: center;
  color: #cbd6ff;
}

.empty-state button {
  margin-top: 1rem;
  border-radius: 999px;
  border: none;
  padding: 0.65rem 1.5rem;
  background: rgba(126, 241, 210, 0.2);
  color: #fff;
  cursor: pointer;
}

@media (max-width: 900px) {
  .dream-panel,
  .interpretation-panel {
    grid-column: span 1;
  }

  .hero-card {
    grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
  }
}
